import React,{useEffect} from 'react'
import ChatMessage from './ChatMessage';
import {useDispatch, useSelector} from 'react-redux'
import {generateRandomname} from '../utils/helper'
import { addMessages } from '../utils/chatslice';

const Livechat = () => {

  const dispatch=useDispatch();
  const chatMessage=useSelector((store)=>store.chat.messages);
  useEffect(()=>{
    const i= setInterval(()=>{
      dispatch(addMessages({
        name:generateRandomname(),
        message:'This is Live chat example'
      }));
    },2000);
    return ()=>{clearInterval(i)};
  },[])
  return (
    <div className='ml-2 p-2 border border-black w-full h-[600px] rounded-lg bg-slate-100 overflow-y-scroll'>
      {chatMessage.map((index,c)=>(<ChatMessage key={index} name={c.name} message={c.message}/>))}
      {console.log()}
    </div>
    
  )
}

export default Livechat;
